    "sql": "CREATE TABLE Tasks (\n  id TEXT PRIMARY KEY,         -- Unique identifier for each task\n  task TEXT NOT NULL,           -- Description of the task\n  user_id TEXT NOT NULL,        -- Foreign key referencing Users table\n  completed BOOLEAN DEFAULT false, -- Status of the task (default to incomplete)\n  createdAt TEXT DEFAULT (datetime('now')),  -- Timestamp for task creation\n  updatedAt TEXT DEFAULT (datetime('now')),  -- Timestamp for last update (can be updated with triggers)\n  dueDate TEXT,                 -- Optional due date for the task\n  description TEXT,\n  FOREIGN KEY (user_id) REFERENCES users(id) ON DELETE CASCADE\n)"
